import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchDogs } from './redux/actions';

function App({ isFetching, src, getDogs }) {
  return (
    isFeatching ? <p>Loading...</p>
      : (
        <div style={ { width: 500 } }>
          <button
            style={ { display: 'block' } }
            onClick={ getDogs }
            type="button"
          >
            New Dog
          </button>
          <img style={ { width: '100%' } } src={ src } alt="dog" />
        </div>
      )
  );
}

const mapStateToProps = (state) => ({
  isFetching: state.isFetching,
  src: state.imagePath,
});

const mapDispatchToProps = (dispatch) => ({
  getDogs: () => dispatch(fetchDogs()),
});

App.propTypes = {
  isFeatching: PropTypes.bool.isRequired,
  src: PropTypes.string.isRequired,
  getDogs: PropTypes.func.isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
